import React from 'react'
import styled from 'styled-components'
import './styles.css'

const TarefaList = styled.ul`
  padding: 0;
  width: 200px;
`

const Tarefa = styled.li`
  text-align: left;
  text-decoration: ${({completa}) => (completa ? 'line-through' : 'none')};
`

const InputsContainer = styled.div`
  display: grid;
  grid-auto-flow: column;
  gap: 10px;
`

class App extends React.Component {
    state = {
      tarefas: [
        {
          id: Date.now(),
          texto: 'Texto da primeira tarefa',
          completa: false 
        },
        {
          id: Date.now(), 
          texto: 'Texto da segunda tarefa',
          completa: true 
        }
],
      inputValue: '',
      filtro: 'completas , pendentes'
    }

  componentDidUpdate() {
    localStorage.setItem("valorInput". this.state.inputValue)
    
  
  };

  componentDidMount() {
    this.pegarMensagens();
  };

  onChangeInput = (event) => {
    this.setState ({inputValue: event.target.value});
  }

  limpar = () => {
    this.setState({inputValue: ""})
  };

  criaTarefa = () => {

    const novaTarefa = {
      id: Date.now(),
      texto: this.state.inputValue,
      completa: false
    }

    const novaLista = [ novaTarefa, ...this.state.tarefas]

    localStorage.setItem("tarefinhas", JSON.stringify(novaLista))

    this.setState({tarefas: novaLista})
    this.limpar();
  }

  pegarMensagens = () => {
    const tarefaNova = JSON.parse(localStorage.getItem("tarefinhas"))
    this.setState({tarefas: tarefaNova})
  }

  selectTarefa = (id) => {

     const  listaNova = this.state.tarefas.map((tarefa) => {
       if (id === tarefa.id) {
         const tarefaNova = {
           ... tarefa, 
           completa: !tarefa.completa
         }
         return tarefaNova
       } else {
         return tarefa
       }
     })
     this.setState({tarefas: listaNova})
     
  }

  onChangeFilter = (event) => {
    this.setState ({filtro: event.target.value})
  }

  render() {
    const listaFiltrada = this.state.tarefas.filter(tarefa => {
      switch (this.state.filtro) {
        case 'pendentes':
          return !tarefa.completa
        case 'completas':
          return tarefa.completa
        default:
          return true
      }
    })

    return (
      <div className="App">
        <h1>Lista de tarefas</h1>
        <InputsContainer>
          <input value={this.state.inputValue} onChange={this.onChangeInput}/>
          <button onClick={this.criaTarefa}>Adicionar</button>
        </InputsContainer>
        <br/>

        <InputsContainer>
          <label>Filtro</label>
          <select value={this.state.filter} onChange={this.onChangeFilter}>
            <option value="">Nenhum</option>
            <option value="pendentes">Pendentes</option>
            <option value="completas">Completas</option>
          </select>
        </InputsContainer>
        <TarefaList>
          {listaFiltrada.map(tarefa => {
            return (
              <Tarefa
                completa={tarefa.completa}
                onClick={() => this.selectTarefa(tarefa.id)}
              >
                {tarefa.texto}
              </Tarefa>
            )
          })}
        </TarefaList>
      </div>
    )
  }
}

export default App
